const slugify = (text) => {
  if (typeof text !== 'string') return '';

  const from = 'àáâäæãåāăąçćčđďèéêëēėęěğǵḧîïíīįìıİłḿñńǹňôöòóœøōõőṕŕřśşšșťțûüùúūǘůűųẃẍÿýžźż·/_,:;'
  const to   = 'aaaaaaaaaacccddeeeeeeeegghiiiiiiiilmnnnnoooooooooprrsssssttuuuuuuuuuwxyyzzz------'

  return text
    .toLowerCase()
    .trim()
    .replace(/\s+/g, '-')
    .replace(new RegExp(`[${from}]`, 'g'), (char) => to[from.indexOf(char)] || '-')
    .replace(/&/g, '-and-')
    .replace(/[^\w\-]+/g, '')
    .replace(/\-\-+/g, '-')
    .replace(/^-+/, '')
    .replace(/-+$/, '')
    .substring(0, 100);
};

module.exports = async function(data) {
  const obj = data.$last;

  async function createSlugPath(node) {
    const parts = [];

    async function traverse(currentNode) {
      if (currentNode.parent) {
        await traverse(currentNode.parent);
      }
      parts.push(slugify(currentNode.title));
    }

    await traverse(node);
    return parts.join('/') ;
  }

  return await createSlugPath(obj);
};
